    // Ambil dan parsing data pengguna dari localStorage
    const user: User = JSON.parse(localStorage.getItem('user') || '{}');

    // Fungsi untuk memeriksa izin
    const hasPermission = (requiredPermissions: string[]): boolean => {
        return user?.permissions?.some((permission) => requiredPermissions?.includes(permission));
    };

    const userRoles = user?.roles;

    // Cek apakah user memiliki peran "Employee" dan "Owner"
    const hasEmployeeRole = userRoles?.includes('Employee');
    const hasOwnerRole = userRoles?.includes('Owner');
    const hasAdminRole = userRoles?.includes('Admin');

    const pathnames = location.pathname.split('/').filter((x) => x);

