    // const storeSlug = hasEmployeeRole && hasOwnerRole ? user?.stores?.[0]?.slug : pathnames[0];
    const storeSlug = hasEmployeeRole ? user?.stores?.[0]?.slug : pathnames[0];
    const isOwnerEmployee = hasEmployeeRole && hasOwnerRole;

    const storeId = storeSlug;

    const isStoreSlugMatch = user.stores?.some((store) => store?.slug === storeSlug);

